#include "HSS.h"

void Paillier_Gen(PK &pk, ZZ &d)
{
    ZZ p, q, Phi_N, temp1, temp2, h;

    // GenGermainPrime(p, 1536); // safe prime
    p = conv<ZZ>("");
    // 1024
    // p = conv<ZZ>("");
    // GenGermainPrime(q, 1536);
    q = conv<ZZ>("");
    // q = conv<ZZ>("");
    mul(pk.N, p, q);        // N = p * q
    mul(pk.N2, pk.N, pk.N); // N^2

    mul(Phi_N, p-1, q-1); // phi(N)

    InvMod(temp1, Phi_N, pk.N);
    mul(temp1, temp1, Phi_N);
    mul(temp2, pk.N, Phi_N);
    rem(d, temp1, temp2);

    pk.DJN_OPEN = true;
    pk.Pre_OPEN = true;
    if (pk.DJN_OPEN)
    {
        RandomBnd(h, pk.N);
        MulMod(h, -h, h, pk.N2);
        PowerMod(pk.hs, h, pk.N, pk.N2);
    }
}

void Pailler_Enc(ZZ &ct, const PK &pk, const ZZ &x)
{
    ZZ r, temp1, temp2;
    if (pk.DJN_OPEN)
    {
        if (pk.Pre_OPEN)
        {
            temp1 = conv<ZZ>("");
        }
        else
        {
            ZZ r_bnd = conv<ZZ>("");
            RandomBnd(r, r_bnd);
            PowerMod(temp1, pk.hs, r_bnd, pk.N2);
        }
    }
    else
    {
        RandomBnd(r, pk.N2);
        PowerMod(temp1, r, pk.N, pk.N2);
    }
    // PowerMod(temp2, pk.N+1,x,pk.N2);
    MulMod(temp2, pk.N, x, pk.N2);
    add(temp2, temp2, 1);
    MulMod(ct, temp1, temp2, pk.N2);
}

void Pailler_Dec(ZZ &x, const PK &pk, const ZZ &sk, const ZZ &ct)
{
    ZZ temp;
    PowerMod(temp, ct, sk, pk.N2);
    sub(temp, temp, 1);
    div(x, temp, pk.N);
}

void HSS_Gen(PK &pk, EK &ek0, EK &ek1)
{
    ZZ d, temp1, temp2;
    Vec<ZZ> d_Bsk;

    Paillier_Gen(pk, d);

    pk.k = 256;
    pk.Bmsg = power2_ZZ(pk.k);
    pk.Bsk = power2_ZZ(2048-pk.k);

    temp1 = d;
    while (temp1 != 0)
    {
        // temp2 = temp1 % Bsk; temp1 = temp1 / Bsk
        DivRem(temp1, temp2, temp1, pk.Bsk);
        d_Bsk.append(temp2);
    }
    pk.l = d_Bsk.length();
    // cout << pk.l << endl;

    mul(temp1, pk.Bmsg, pk.Bsk);
    pk.D.SetLength(pk.l);
    int i;
    for (i = 0; i < pk.l; ++i)
    {
        RandomBnd(temp2, temp1);
        ek1.d_Bsk.append(temp2);
        sub(temp2, temp2, d_Bsk[i]);
        ek0.d_Bsk.append(temp2);
        Pailler_Enc(pk.D[i], pk, d_Bsk[i]);
    }
}

void HSS_Free(PK &pk, EK &ek0, EK &ek1)
{
    pk.D.kill();
    ek0.d_Bsk.kill();
    ek1.d_Bsk.kill();
}

void HSS_Input(Vec<ZZ> &Ix, const PK &pk, const ZZ &x)
{
    if (!Ix.length())
    {
        Ix.SetLength(pk.l + 1);
    }
    ZZ r, temp1, temp2;
    Pailler_Enc(Ix[0], pk, x);

    int i;
    for (i = 0; i < pk.l; ++i)
    {
        if (pk.DJN_OPEN)
        {
            if (pk.Pre_OPEN)
            {
                temp1 = conv<ZZ>("");
            }
            else
            {
                ZZ r_bnd = conv<ZZ>("");
                RandomBnd(r, r_bnd);
                PowerMod(temp1, pk.hs, r_bnd, pk.N2);
            }
        }
        else
        {
            RandomBnd(r, pk.N2);
            PowerMod(temp1, r, pk.N, pk.N2);
        }
        PowerMod(temp2, pk.D[i], x, pk.N2);
        MulMod(Ix[i + 1], temp1, temp2, pk.N2);
    }
}

void HSS_M1Gen(Vec<ZZ> &M1, int b, const PK &pk, const EK &ek, int &prf_key) 
{
    ZZ temp1;
    temp1 = PRF_ZZ(prf_key++, pk.Bmsg);

    if (b == 0)
    {
        M1.append(temp1);
    }
    else
    {
        AddMod(temp1, temp1, ZZ(1), pk.N);
        M1.append(temp1);
    }
    M1.append(ek.d_Bsk);
}


void HSS_ConvertInput(Vec<ZZ> &Mx, int b, const PK &pk, const EK &ek, const Vec<ZZ> &Ix, int &prf_key)
{
    Vec<ZZ> M1;
    HSS_M1Gen(M1, b, pk, ek, prf_key); 
    HSS_Mul(Mx, pk, ek, Ix, M1, prf_key);
}

void HSS_Mul(Vec<ZZ> &Mz, const PK &pk, const EK &ek, const Vec<ZZ> &Ix, const Vec<ZZ> &My, int &prf_key)
{
    if (!Mz.length())
    {
        Mz.SetLength(pk.l + 1);
    }

    ZZ temp1, temp2, temp3;
    temp1 = 1;
    temp3 = My[1];
    int i;
    for (i = 1; i < pk.l; ++i)
    {
        mul(temp1, temp1, pk.Bsk);
        // power(temp1, pk.Bsk, i);
        mul(temp2, temp1, My[i + 1]);
        add(temp3, temp2, temp3);
    }

    for (i = 0; i < pk.l + 1; ++i)
    {
        PowerMod(temp1, Ix[i], temp2, pk.N2);
        HSS_DDLog(Mz[i], pk, temp1);
        AddMod(Mz[i], Mz[i], PRF_ZZ(prf_key++, pk.N), pk.N);
    }
}

void HSS_DDLog(ZZ &z, const PK &pk, const ZZ &g)
{
    ZZ h1, h, temp1;
    DivRem(h1, h, g, pk.N); // h = g % N; h1 = g / N
    InvMod(temp1, h, pk.N);
    MulMod(z, h1, temp1, pk.N);
}

void HSS_AddMemory(Vec<ZZ> &Mz, const PK &pk, const Vec<ZZ> &Mx, const Vec<ZZ> &My)
{
    if (!Mz.length())
    {
        Mz.SetLength(pk.l + 1);
    }
    int i;
    for (i = 0; i < pk.l + 1; ++i)
    {
        add(Mz[i], Mx[i], My[i]);
    }
}

void HSS_SubMemory(Vec<ZZ> &Mz, const PK &pk, const Vec<ZZ> &Mx, const Vec<ZZ> &My)
{
    if (!Mz.length())
    {
        Mz.SetLength(pk.l + 1);
    }
    int i;
    for (i = 0; i < pk.l + 1; ++i)
    {
        SubMod(Mz[i], Mx[i], My[i], pk.N);
    }
}

void HSS_AddInput(Vec<ZZ> &Iz, const PK &pk, const Vec<ZZ> &Ix, const Vec<ZZ> &Iy)
{
    double tt =GetTime();
    if (!Iz.length())
    {
        Iz.SetLength(pk.l + 1);
    }

    int i;
    for (i = 0; i < pk.l + 1; ++i)
    {
        MulMod(Iz[i], Ix[i], Iy[i], pk.N2);
    }
    //cout << 1000*(GetTime() - tt)<<endl;
}

void HSS_Evaluate(Vec<ZZ> &y_b_res, int b, const Mat<ZZ> &Ix, const PK & pk, const EK &ekb, int &prf_key, 
const vector<vector<int>> &F_TEST)
{
    y_b_res.kill();
    y_b_res.SetLength(pk.l + 1);
    Vec<ZZ> M1;
    HSS_M1Gen(M1, b, pk, ekb, prf_key); 

    Vec<ZZ> Monomial;
    int i, j, k;
    for (i = 0; i < F_TEST.size(); ++i)
    {
        Monomial = M1;
        for (j = 0; j < Ix.NumRows(); ++j)
        {
            for (k = 0; k < F_TEST[i][j]; ++k)
            {
                HSS_Mul(Monomial, pk, ekb, Ix[j], Monomial, prf_key);
            }
        }
        add(y_b_res, y_b_res, Monomial);
    }
}
